    const [value, setValue] = useState("")
    const [error, setError] = useState("Algo deu errado!")
    const [chatHistory, setChatHistory] = useState([])

    const surpriseOptions = [
      'Quem ganhou o ultimo premio Nobel da paz?',
      'De onde vem a Pizza?',
      'Qual a historia do Linux?'
    ]

    const surprise = () => {
      const randomValue = surpriseOptions[Math.floor(Math.random() * surpriseOptions.length)]
      setValue(randomValue)
    }

    const getResponse = async () => {

        if(!value) {
          setError("Erro! Por favor faça uma pergunta!")
        }

    }

  return (

      <div className="app">
        <p>Boa tarde, jovem!
          <button className="surprise" onClick={surprise} disabled={!chatHistory}>Me surpreenda</button>
        </p>
          <div className="input-container">
            <input
                value={value}
                placeholder="Osvaldo"
                onChange={(e) => setValue(e.target.value)}
            />
            {!error && <button>Me pergunte</button>}
            {error &&<button>Limpar</button>}
          </div>
          {error && <p>{error}</p>}
          <div className="search-result">
